import { Component } from '@angular/core';

@Component({
  selector: 'app-componente-pai',
  templateUrl: './componente-pai.component.html',
  styleUrls: ['./componente-pai.component.css']
})
export class ComponentePaiComponent {
  sobrenome = "da Silva";

  mostraNomeCompleto(nomeCompleto: any){
    alert(`Nome Completo: ${nomeCompleto}`)
  }
}
<h3>Componente-pai!</h3>
<p> - Sobrenome definido no elemento pai: <strong>{{sobrenome}}</strong></p>
<p>nome completo e: <strong>{{filho.nome}}  {{sobrenome}}</strong></p>
<app-componente-filho [sobrenome]="sobrenome" (mostraNome)="mostraNomeCompleto($event)" #filho></app-componente-filho>
